    private void plusminusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_plusminusActionPerformed
        // TODO add your handling code here:
        if(value1 != 0 && value1 > 0 && value2 == 0) {
            value1 = Integer.parseInt("-" + value1);
            resultTxt.setText(String.valueOf(value1));
        } else if(value1 != 0 && value1 < 0 && value2 == 0) {
            value1 = value1 * -1;
            resultTxt.setText(String.valueOf(value1));
        } else if(value2 != 0 && value1 != 0 && value2 > 0) {
            value2 = Integer.parseInt("-" + value2);
            resultTxt.setText(value1 + " " + getStringOperation(operator) + " " + value2);
        } else if(value2 != 0 && value1 != 0 && value2 < 0) {
            value2 = value2 * -1;
            resultTxt.setText(value1 + " " + getStringOperation(operator) + " " + value2);
        }
    }//GEN-LAST:event_plusminusActionPerformed

