    private void btnequalsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnequalsActionPerformed
        // TODO add your handling code here:
        double answer = 0;
        switch(operator) {
            case "plus":
                answer = value1 + value2;
                resultTxt.setText(String.valueOf(answer));
                value1 = (int) answer;
                break;
            case "minus":
                answer = value1 - value2;
                resultTxt.setText(String.valueOf(answer));
                value1 = (int) answer;
                break;
            case "multiply":
                answer = value1 * value2;
                resultTxt.setText(String.valueOf(answer));
                value1 = (int) answer;
                break;
            case "divide":
                answer = value1 / value2;
                resultTxt.setText(String.valueOf(answer));
                value1 = (int) answer;
                break;
            case "square":
                answer = Math.pow(value1, value2);
                resultTxt.setText(String.valueOf(answer));
                value1 = (int) answer;
                break;
            case "logarithm":
                answer = value1 * Math.log10(value2);
                resultTxt.setText(String.valueOf(answer));
                value1 = (int) answer;
                break;
            case "sqrt":
                answer = value1 * Math.sqrt(value2);
                resultTxt.setText(String.valueOf(answer));
                value1 = (int) answer;
                break;
            case "sin":
                answer = value1 * Math.sin(value2);
                resultTxt.setText(String.valueOf(answer));
                value1 = (int) answer;
                break;
            case "cos":
                answer = value1 * Math.cos(value2);
                resultTxt.setText(String.valueOf(answer));
                value1 = (int) answer;
                break;
            case "tan":
                answer = value1 * Math.tan(value2);
                resultTxt.setText(String.valueOf(answer));
                value1 = (int) answer;
                break;
            default:
                break;
        }
    }//GEN-LAST:event_btnequalsActionPerformed

