    private String getStringOperation(String opt) {
        String s = null;
        switch(operator) {
            case "plus":
                s = "+";
                break;
            case "minus":
                s = "-";
                break;
            case "multiply":
                s = "*";
                break;
            case "divide":
                s = "/";
                break;
            case "square":
                s = "^";
                break;
            case "logarithm":
                s = "log";
                break;
            case "sqrt":
                s = "sqrt";
                break;
            case "sin":
                s = "sin";
                break;
            case "cos":
                s = "cos";
                break;
            case "tan":
                s = "tan";
                break;
            default:
                break;
        }
        return s;
    }
    
